mixin pseudo(className)
    .contacts__pseudo.pseudo-lines(class=className)
        span(v-for="item in 3")

mixin dataList(data, className)    
    .contacts__list.op-300(class=className)
        each item,index in data
            article        
                +pseudo('pos-a-l')
                +pseudo('pos-a-r')
                +svglist(index)
                div !{item.text}

mixin both 
    .contacts__wrap.lh-n
        .contacts__title.op-800.h2-regular= info.title            
        .contacts__main            
            +logo
            .contacts__line.desk
            +dataList(info.list.desk, 'desk')
            +dataList(info.list.mob, 'mob')

section.contacts#contacts
    +both
